import React from 'react';
import { useSelector } from 'react-redux';
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useGetTodosQuery } from '../api/todosApi';
import { Todo } from '../types/Todo';
import NewTodo from '../components/Todo';
import styled from 'styled-components';

const DashboardContainer = styled.div`

display:flex;
flex-direction:row;
justify-content flex-start;
align-items: flex-start;
flex-wrap: wrap;

gap: 20px;
width: 1440px;

margin: 0 auto;
padding: 20px 20px;
height:100%;
`;




const Dashboard = () => {
  const token = localStorage.getItem('token');

 const { data:todos, error, isLoading, isSuccess } = useGetTodosQuery();





 if (isSuccess && todos.myTodos && todos.myTodos.length > 0 ) {
  console.log(todos.myTodos);
}


  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate('/login');
    }
  }, [token, navigate]);


  if (!token) {
    return null;
  }

  if (isLoading) {
    return <div>Todos are loading...</div>;
  }

  if (error) {
    return <div>An error occurred, please refresh the page</div>;
  }

  return (
    <>
    <DashboardContainer>
    <h3>Todos:</h3>
    {isSuccess && todos.myTodos && todos.myTodos.length > 0 ? (
      <ul>
        {todos.myTodos.map((todo: Todo) => (
          <li key={todo._id}>
            <NewTodo todo={todo} />
          </li>
        ))}
      </ul>
    ) : (
      <p>No todos found</p>
    )}
    </DashboardContainer>
  </>
  );
};

export default Dashboard;
