import React, { useState, useEffect } from "react";
import checkedIcon from "../icons/checked.svg";
import uncheckedIcon from "../icons/unchecked.svg";


const Check: React.FC<any> = ({ completed, onChecked }) => {
    const [checked, setChecked] = useState(completed);
  
    useEffect(() => {
      setChecked(completed);
    }, [completed]);
  
    const handleCheck = () => {
      setChecked(!checked);
      onChecked();
    };
  
    return (
      <>
        {checked ? (
          <img src={checkedIcon} alt="checked" onClick={handleCheck} />
        ) : (
          <img src={uncheckedIcon} alt="unchecked" onClick={handleCheck} />
        )}
      </>
    );
  };
