import React from 'react';
import { useSelector } from 'react-redux';
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useGetTodosQuery } from '../api/todosApi';

const Dashboard = () => {
  const token = localStorage.getItem('token');
  const { data: todos, error, isLoading, isSuccess } = useGetTodosQuery();
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate('/login');
    }
  }, [token, navigate]);

  if (!token) {
    return null;
  }

  if (isLoading) {
    return <div>Todos are loading...</div>;
  }

  if (error) {
    return <div>An error occurred, please refresh the page</div>;
  }

  return (
    <div>
      <h3>Todos:</h3>
      {isSuccess && todos && todos.length > 0 ? (
        <ul>
          {todos.map((todo) => (
            <li key={todo._id}>
              <strong>{todo.title}</strong>
              {todo.tasks.length > 0 ? (
                <ul>
                  {todo.tasks.map((task) => (
                    <li key={task._id}>{task.text}</li>
                  ))}
                </ul>
              ) : (
                <p>No tasks found</p>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No todos found</p>
      )}
    </div>
  );
};

export default Dashboard;
