import React from 'react';
import styled from 'styled-components';
import { Link, useNavigate } from 'react-router-dom';
import { useState,useEffect } from 'react';

const Header = styled.header`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  padding: 10px 10%;
  background-color: white;
`;

const Logo = styled.div`
  margin-right: auto;
`;

const NavLinks = styled.ul`
  list-style: none;
  display: flex;
`;

const NavLink = styled.li`
  padding: 0px 20px;

  a {
    font-family: 'Montserrat', sans-serif;
    font-weight: 500;
    color: #edf0f1;
    text-decoration: none;
    transition: all 0.3s ease 0s;

    &:hover {
      color: #0088a9;
    }
  }
`;

const CTA = styled.button`
  margin-left: 20px;
  padding: 9px 25px;
  background-color: rgba(0, 136, 169, 1);
  border: none;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease 0s;

  &:hover {
    background-color: rgba(0, 136, 169, 0.8);
  }
`;

const Menu = styled.div`
  display: none;

  @media only screen and (max-width: 800px) {
    display: initial;
  }
`;

const Overlay = styled.div`
  height: 100%;
  width: 0;
  position: fixed;
  z-index: 1;
  left: 0;
  top: 0;
  background-color: #24252a;
  overflow-x: hidden;
  transition: all 0.5s ease 0s;

  &.overlay--active {
    width: 100%;
  }
`;

const OverlayContent = styled.div`
  display: flex;
  height: 100%;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const OverlayLink = styled.a`
  padding: 15px;
  font-size: 36px;
  display: block;
  transition: all 0.3s ease 0s;

  &:hover,
  &:focus {
    color: #0088a9;
  }
`;

const OverlayClose = styled.div`
  position: absolute;
  top: 20px;
  right: 45px;
  font-size: 60px;
  color: #edf0f1;
  cursor: pointer;
`;

const Navbar = () => {

    const navigate = useNavigate();


    const token = localStorage.getItem('token');

    const logout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    }

    const [user, setUser] = useState<any>(null);

    useEffect(() => {
        

    }, [token,user]);


  return (
    <Header>
      <Logo>Logo</Logo>
      <NavLinks>
        <NavLink>
          <a href="#">Home</a>
        </NavLink>
        <NavLink>
          <a href="#">About</a>
        </NavLink>
        <NavLink>
          <a href="#">Contact</a>
        </NavLink>
      </NavLinks>
      <CTA>Get Started</CTA>
      <Menu>â˜°</Menu>
      <Overlay>
        <OverlayContent>
          <OverlayLink href="#">Home</OverlayLink>
          <OverlayLink href="#">About</OverlayLink>
          <OverlayLink href="#">Contact</OverlayLink>
        
        </OverlayContent>
      </Overlay>
    </Header>
  );
};

export default Navbar;
