import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Task } from "../types/Task";
import Check from "./Check";
import { useUpdateTaskStatusMutation } from "../api/todosApi";

const RowContainer = styled.div`
  width: 100%;
  height: 30px;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  gap: 5px;
`;

const Row = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  gap: 10px;
`;

const RowIcon = styled.div`
  width: 30px;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const RowText = styled.div<{ completed: boolean }>`
  width: calc(100% - 20px);
  height: 100%;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  font-size: 14px;
  font-family: "Poppins", sans-serif;
  font-weight: 400;
  color: #9599b5;
  text-decoration: ${(props) => (props.completed ? "line-through" : "none")};
`;

interface TaskListProps {
  tasks: Task[];
}

const TaskList: React.FC<TaskListProps> = ({ tasks }) => {
  const [allTasks, setAllTasks] = useState<Task[]>(tasks);

  console.log(tasks)


  const [updateTaskStatus] = useUpdateTaskStatusMutation();



  const handleTaskCheck = async (todo: string, taskId: string, completed: boolean) => {
    await updateTaskStatus({ todo, taskId, completed })
      .unwrap()
      .then((res) => {
       const updatedTask = res.updatedTodoTask;
       const updatedTasks = allTasks.map((task) => {
          if (task._id === updatedTask._id) {
            return updatedTask;
          } else {
            return task;
          }
        });
        
;
      })
      .catch((err) => {
        console.log(err);
      });
  };
  
  

  return (
    <RowContainer>
      {allTasks.map((task) => (
        <Row key={task._id}>
          <RowIcon>
            <Check
              completed={task.completed}
              onChange={() => handleTaskCheck(task.todo, task._id, task.completed)}
            />
          </RowIcon>
          <RowText completed={task.completed}>{task.text}</RowText>
        </Row>
      ))}
    </RowContainer>
  );
};

export default TaskList;
