import React from 'react';
import { useSelector } from 'react-redux';
import {useEffect, useState} from 'react';
import { useNavigate } from 'react-router-dom';
import { useGetTodosQuery } from '../api/todosApi';

const Dashboard = () => {
    const token = localStorage.getItem('token');
    const { data, error, isLoading } = useGetTodosQuery();


    const navigate = useNavigate();

    useEffect(() => {
        if (!token) {
            navigate('/login');
        }
    }
    , [token, navigate]);

    if (!token || !data) {
      return null; 
    }
    
    if (isLoading) {
      return <div>Todolar yükleniyor...</div>;
    }

    if (error) {
      return <div>Bir hata oluştu, lütfen sayfayı yenileyin</div>;
    }

  return (
    <div>
      <h1>Welcome to Dashboard</h1>
      <h2>{token}</h2>

      <h3>Todos:</h3>
      {todos && todos.length > 0 ? (
        <ul>
          {todos.map((todo) => (
            <li key={todo.id}>
              <strong>{todo.title}</strong>
              <ul>
                {todo.tasks.map((task) => (
                  <li key={task.id}>{task.title}</li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      ) : (
        <p>No todos found</p>
      )}

      {/* Buraya todo ekleme ve diğer işlemler için gerekli form ve butonları ekleyebilirsiniz */}
    </div>
  
  );
};

export default Dashboard;
