import React from "react";
import { useState, useEffect } from "react";
import styled from "styled-components";
import { Task } from "../types/Task";




const RowContainer = styled.div`
  width: 100%;
  height: 30px;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
`;

const Row = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
`;

const RowIcon = styled.div`
  width: 30px;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const RowText = styled.div<{ completed: boolean }>`
  width: calc(100% - 20px);
  height: 100%;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  font-size: 14px;
  font-family: "Poppins", sans-serif;
  font-weight: 400;
  color: #9599b5;
  text-decoration: ${(props) => (props.completed ? "line-through" : "none")};
`;

interface TaskListProps {
  tasks: Task[];
}



const TaskList: React.FC<TaskListProps> = ({tasks}) => {


  return (
    <RowContainer>
      {tasks.map((task) => (
        <Row key={task._id}>
          <RowIcon>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill={task.completed ? "#AF7EEB" : "#000000"}
              className="bi bi-check2"
              viewBox="0 0 16 16"
            >
              <path
                fillRule="evenodd"
                d="M4.146 8.354a.5.5 0 00.708 0L7.5 6.207l4.146 4.147a.5.5 0 00.708-.708l-4.5-4.5a.5.5 0 00-.708 0l-2.5 2.5a.5.5 0 000 .708z"
              />
            </svg>
          </RowIcon>
          <RowText completed={task.completed}>{task.text}</RowText>
        </Row>
      ))}
    </RowContainer>
  );
}

export default TaskList;
