import React from 'react'
import {useState, useEffect} from 'react'
import styled from 'styled-components'
import TaskList from './TaskList'
import { Todo } from '../types/Todo'
import { Icon } from '@iconify/react';
import deleteTodoIcon from '../icons/delete-todo.svg'
import editTodoIcon from '../icons/edit-todo.svg'
import { useDeleteTodoMutation } from '../api/todosApi'




const Container = styled.div`
    width:280px;
    height:250px;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:center;
    gap: 15px;
    position: relative;

    `


    const AddTask = styled.div`
    width:120px;
    height:40px;
    display:flex;
    justify-content:center;
    align-items:center;
    font-size:14px;
    font-family: 'Montserrat', sans-serif;
    font-weight: 600;
    background-color: #AF7EEB;
    color: #fff;
    border-radius: 15px;
    box-shadow: 0px 15px 15px rgba(0,0,0,0.2);
    position: absolute;
    bottom: -15px;
    left: 50%;
    transform: translateX(-50%);
    &:hover {
        cursor: pointer;
        background-color: #9A6EEB;
    }


    `

    const TitleContainer = styled.div`
    width:100%;
    height:45px;
    display:flex;
    justify-content:center;
    align-items:center;
    flex-direction:row;
    background-color: #AF7EEB;
    



    `

    const Menu = styled.div`
    width:40px;
    height:100%;
    display:flex;
    justify-content:center;
    align-items:center;

    &:hover {
        cursor: pointer;
        background-color: #9A6EEB;
    }


    `


    const DropdownMenuContainer = styled.div`
    width:100px;
    height:auto;
    display:flex;
    flex-direction:column;
    justify-content:flex-start;
    align-items:flex-start;
    position: absolute;
    top: 45px;
    left: 0;
    z-index: 100;


    `

    const DropdownMenu = styled.ul`
    width:100%;
    height:auto;
    display:flex;
    flex-direction:column;
    justify-content:flex-start;
    align-items:flex-start;
    list-style:none;
    border-radius: 15px;
    box-shadow: 0px 10px 15px rgba(0,0,0,0.1);
    background-color: #fff;


    `
    const DropdownItem = styled.li`
    width:100%;
    height:40px;
    display:flex;
    justify-content:flex-start;
    align-items:center;
    font-size:14px;
    font-family: 'Montserrat', sans-serif;
    font-weight: 500;
    color: #000;
    padding-left: 15px;
    border-radius: inherit;
    &:hover {
        cursor: pointer;
        background-color: #9A6EEB;
    }

    `


    const Title = styled.div`
    width: calc(100% - 40px);
    height: 100%;
    display:flex;
    justify-content:center;
    align-items:center;
    font-size: 20px;
    font-family: 'Montserrat', sans-serif;
    font-weight: 600;
    color: #fff;
    `
    const ContentContainer = styled.div`
    width:100%;
    height:190px;
    display:flex;
    flex-direction:column;
    justify-content:flex-start;
    align-items:flex-start;
    padding: 30px;
    background-color: #fff;
    box-shadow: 0px 10px 15px rgba(0,0,0,0.1);

    `

    
interface NewTodoProps {
    todo: Todo;
  }

function NewTodo({ todo }: NewTodoProps
    ) {
  return (
    <div>
        <Container>
            <TitleContainer>
                <Men onClick>
                <Icon icon="ci:hamburger-md" color="white" width="30" height="30" />
                 <DropdownMenuContainer>

                    <DropdownMenu>
                        <DropdownItem>
                        <img src={editTodoIcon} alt="edit todo" width="24" height="24"/>
                            Edit
                        </DropdownItem>
                        <DropdownItem>
                        <img src={deleteTodoIcon} alt="delete todo" width="24" height="24"/>
                            Delete
                        </DropdownItem>
                    </DropdownMenu>
                    </DropdownMenuContainer>

                </Men>
                <Title>
                    {todo.title}
                </Title>
            </TitleContainer>
            <ContentContainer>
                <TaskList tasks={todo.tasks}/>
                
                </ContentContainer>
                <AddTask>
                    Add Task
                </AddTask>


    </Container>


    </div>
  )
}

export default NewTodo
