import React from 'react';
import { useSelector } from 'react-redux';
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useGetTodosQuery } from '../api/todosApi';
import { Todo } from '../types/Todo';

const Dashboard = () => {
  const token = localStorage.getItem('token');

 const { data:todos, error, isLoading, isSuccess } = useGetTodosQuery(token);
  const [todosData, setTodosData] = React.useState<Todo[]>([]);


  console.log(todos);
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate('/login');
    }
  }, [token, navigate]);

  useEffect(() => {
    if (isSuccess && todos) {
      setTodosData(prev => [...prev, ...todos]);
    }
  }, [isSuccess, todos]);

  if (!token) {
    return null;
  }

  if (isLoading) {
    return <div>Todos are loading...</div>;
  }

  if (error) {
    return <div>An error occurred, please refresh the page</div>;
  }

  return (
    <div>
