import React from 'react'
import {useState, useEffect} from 'react'
import styled from 'styled-components'
import TaskList from './TaskList'
import { Todo } from '../types/Todo'
import { Icon } from '@iconify/react';



const Container = styled.div`
    width:280px;
    height:250px;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:center;
    column-gap: 15px;

    `

    const TitleContainer = styled.div`
    width:100%;
    height:45px;
    display:flex;
    justify-content:center;
    align-items:center;
    flex-direction:row;
    background-color: #AF7EEB;



    `

    const Menu = styled.div`
    width:40px;
    height:100%;
    display:flex;
    justify-content:center;
    align-items:center;

    `

    const Title = styled.div`
    width: calc(100% - 40px);
    height: 100%;
    display:flex;
    justify-content:center;
    align-items:center;
    font-size: 20px;
    font-family: 'Montserrat', sans-serif;
    font-weight: 600;
    color: #fff;
    `
    const ContentContainer = styled.div`
    width:100%;
    height:190px;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:center;
    padding: 30px;
    background-color: #fff;
    box-shadow: 0px 10px 15px rgba(0,0,0,0.1);

    `

    
interface NewTodoProps {
    todo: Todo;
  }

function NewTodo({ todo }: NewTodoProps
    ) {
  return (
    <div>
        <Container>
            <TitleContainer>
                <Menu>
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="#fff" className="bi bi-list" viewBox="0 0 16 16">
                        <path fillRule="evenodd" d="M2 3.5A1.5 1.5 0 013.5 2h9A1.5 1.5 0 0114 3.5v9a1.5 1.5 0 01-1.5
                        1.5h-9A1.5 1.5 0 012 12.5v-9zM3.5 3a.5.5 0 00-.5.5v9a.5.5
                        0 00.5.5h9a.5.5 0 00.5-.5v-9a.5.5
                        0 00-.5-.5h-9z"/>
                        <path fillRule="evenodd" d="M2.5 4a.5.5 0 01.5-.5h9a.5.5
                        0 01.5.5v1a.5.5 0 01-.5.5h-9a.5.5
                        0 01-.5-.5v-1zm.5 7a.5.5 0 000 1h9a.5.5
                        0 000-1h-9z"/>
                        </svg>
                </Menu>
                <Title>
                    {todo.title}

                </Title>
            </TitleContainer>
            <ContentContainer>
                <TaskList tasks={todo.tasks}/>
                
                </ContentContainer>

    </Container>


    </div>
  )
}

export default NewTodo
