import {createApi, fetchBaseQuery} from '@reduxjs/toolkit/query/react';
import { RootState } from '../redux/store';


interface RootStateWithAuth extends RootState {
    auth: {
        token: string | null;
    };
}




const baseQuery = fetchBaseQuery({baseUrl: 'http://localhost:5000',
    prepareHeaders: (headers, {getState}) => {
        const token = (getState() as RootStateWithAuth).auth.token;
        if (token) {
            headers.set('authorization', `Bearer ${token}`);
        }
        return headers;
    },
});


export interface AuthResponse {
    token: string;
    message : string;
}




export const authApi = createApi({
    reducerPath: 'authApi',
    baseQuery,
    endpoints: (builder) => ({
        loginUser : builder.mutation<AuthResponse, { email: string, password: string }>({
            query: (body: {email: string, password: string}) => ({
                url: '/login',
                method: 'POST',
                body,
            }),
        }),
        

    }),
});

export const {useLoginUserMutation} = authApi;
