import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Task } from "../types/Task";
import Check from "./Check";
import { useUpdateTaskStatusMutation } from "../api/todosApi";
import { useDeleteTaskMutation } from "../api/todosApi";
import deleteIcon from "../icons/delete.svg";
import { useDispatch } from "react-redux";
import { updateTask } from "../redux/slices/todoSlice";
import { deleteTask } from "../redux/slices/todoSlice";

const RowContainer = styled.div`
  width: 100%;
  height: 40px;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  gap: 5px;
  position: relative;
`;

const Row = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  gap: 10px;
  position: relative;

  &:hover {
    background-color: #f5f5f5;
    cursor: pointer;
  }
`;

const RowIcon = styled.div`
  width: 30px;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const RowText = styled.div<{ completed: boolean }>`
  width: calc(100% - 20px);
  height: 100%;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  font-size: 14px;
  font-family: "Poppins", sans-serif;
  font-weight: 400;
  color: #9599b5;
  text-decoration: ${(props) => (props.completed ? "line-through" : "none")};
`;

const DeleteButton = styled.div`
  width: 20px;
  height: 20px;
  display: flex;
  justify-content: center;
  align-items: center;
  position: absolute;
  right: 5px;
  top: 50%;
  transform: translateY(-50%);
  transition: all 0.2s ease-in-out;

  opacity: 0;

  ${Row}:hover & {
    opacity: 1;
    transition: all 0.2s ease-in-out;
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

interface TaskListProps {
  tasks: Task[];
}

const TaskList: React.FC<TaskListProps> = ({ tasks }) => {
  const [allTasks, setAllTasks] = useState<Task[]>(tasks);

  const dispatch = useDispatch();

  const [deleteTaskMutation, { isLoading, isError, isSuccess }] =
    useDeleteTaskMutation();

  useEffect(() => {
    const sortedTasks = [...tasks].sort((a, b) => {
      return a.createdAt > b.createdAt ? -1 : 1;
    });

    setAllTasks(sortedTasks);
  }, [tasks]);

  const handleDeleteTask = (todo: string, taskId: string) => {
    dispatch(deleteTask({ taskId: taskId }));
    deleteTaskMutation({ todo, taskId });

    const updatedTasks = allTasks.filter((task) => task._id !== taskId);
    setAllTasks(updatedTasks);
  };

  return (
    <RowContainer>
      {allTasks.map((task) => (
        <Row key={task._id}>
          <RowIcon>
            <Check completed={task.completed} taskId={task._id} todo={task.todo} />
          </RowIcon>
          <RowText completed={task.completed}>{task.text}</RowText>
          <DeleteButton onClick={() => handleDeleteTask(task.todo, task._id)}>
            <img src={deleteIcon} alt="delete" />
          </DeleteButton>
        </Row>
      ))}
    </RowContainer>
  );
};

export default TaskList;
