}

  return (
    <DashboardContainer>
      
      {todosState.length > 0 ? (
        <TodoWrapper>
          {todosState.map((todo: Todo) => (
            <NewTodo key={todo._id} todo={todo} onDelete = {handleDeleteTodo} />
          ))}
        </TodoWrapper>
      ) : (
        <p>No todos found</p>
      )}
       
   
    </DashboardContainer>
  );
};

export default Dashboard;
