import React from "react";
import { useState, useEffect } from "react";
import styled from "styled-components";
import { Task } from "../types/Task";
import Check from "./Check";



const RowContainer = styled.div`
  width: 100%;
  height: 30px;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
`;

const Row = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
`;

const RowIcon = styled.div`
  width: 30px;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const RowText = styled.div<{ completed: boolean }>`
  width: calc(100% - 20px);
  height: 100%;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  font-size: 14px;
  font-family: "Poppins", sans-serif;
  font-weight: 400;
  color: #9599b5;
  text-decoration: ${(props) => (props.completed ? "line-through" : "none")};
`;

interface TaskListProps {
  tasks: Task[];
}



const TaskList: React.FC<TaskListProps> = ({tasks}) => {

  const handleTaskCheck = (taskId: string, completed: boolean) => {

    const updatedTasks = tasks.map((task) => {
      if (task._id === taskId) {
        return { ...task, completed };
      }
      return task;
    }
    );

    
    
  };


  return (
    <RowContainer>
      {tasks.map((task) => (
        <Row key={task._id}>
          <RowIcon>
            <Check completed={task.completed} onChange = {(completed) => handleTaskCheck(task._id, completed)} /> 
          </RowIcon>
          <RowText completed={task.completed}>{task.text}</RowText>
        </Row>
      ))}
    </RowContainer>
  );
}

export default TaskList;
