              {todo.tasks?.length > 0 ? (
                <ul>
                  {todo.tasks?.map((task) => (
                    <li key={task._id}>{task.text}</li>
                  ))}
                </ul>
              ) : (
                <p>No tasks found</p>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No todos found</p>
      )}
    </>
  );
};

export default Dashboard;
