import React from 'react';
import { useSelector } from 'react-redux';
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useGetTodosQuery } from '../api/todosApi';
import { Todo } from '../types/Todo';
import NewTodo from '../components/Todo';
import styled from 'styled-components';

const DashboardContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  row-gap: 20px;
  column-gap: 40px;
  width: 100%;
  max-width: 1440px;
  margin: 0 auto;
  padding: 20px;
  height: 100%;
`;

const TodoWrapper = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 20px;
`;

const Dashboard = () => {
  const token = localStorage.getItem('token');

  const { data: todos, error, isLoading, isSuccess } = useGetTodosQuery();

  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate('/login');
    }
  }, [token, navigate]);

  if (!token) {
    return null;
  }

  if (isLoading) {
    return <div>Todos are loading...</div>;
  }

  if (error) {
    return <div>An error occurred, please refresh the page</div>;
  }

  return (
    <DashboardContainer>
      {isSuccess && todos.myTodos && todos.myTodos.length > 0 ? (
        <TodoWrapper>
          {todos.myTodos.map((todo: Todo) => (
            <NewTodo key={todo._id} todo={todo} />
          ))}
        </TodoWrapper>
      ) : (
        <p>No todos found</p>
      )}
    </DashboardContainer>
  );
};

export default Dashboard;
