import React, { useState } from "react";
import styled from "styled-components";
import checkedIcon from "../icons/checked.svg";
import uncheckedIcon from "../icons/unchecked.svg";
import { useUpdateTaskStatusMutation } from "../api/todosApi";

const Icon = styled.img`
  cursor: pointer;
`;

interface CheckProps {
  completed: boolean;
  todo: string;
  taskId: string;
  onUpdate : (taskId: string, completed: boolean) => void;
}

const Check: React.FC<CheckProps> = ({ completed, todo, taskId, onUpdate }) => {
  const [isChecked, setIsChecked] = useState(completed);
  const [updateTaskStatus] = useUpdateTaskStatusMutation();

  const handleCheck = async () => {
    try {
        await updateTaskStatus({ todo, taskId, completed: !isChecked },

            );
            onUpdate();
        setIsChecked(!isChecked);
      } catch (error) {
        console.log(error);
      }
  };

  return (
    <Icon
      src={isChecked ? checkedIcon : uncheckedIcon}
      alt={isChecked ? "checked" : "unchecked"}
      onClick={handleCheck}
    />
  );
};

export default Check;
