import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useGetTodosQuery } from '../api/todosApi';
import { Todo } from '../types/Todo';
import NewTodo from '../components/Todo';
import styled from 'styled-components';
import SyncLoader from 'react-spinners/SyncLoader';
import { setTodos } from '../redux/slices/todoSlice';


const DashboardContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  
  width: 100%;
  max-width: 1440px;
  margin: 0 auto;
  padding: 20px;
  height: 100%;
`;

const TodoWrapper = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 40px;
`;

const Dashboard = () => {
  const token = localStorage.getItem('token');

  const { data: todos, error, isLoading, isSuccess } = useGetTodosQuery();

  const [allTodos, setAllTodos] = useState<Todo[]>([]);

  const todosState = useSelector((state: any) => state.todos);


const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    if (isSuccess) {
      dispatch(setTodos(todos.myTodos));
    }
    if (todosState) {
      setAllTodos(todosState);
    }



  }, [dispatch, isSuccess, todos, todosState]);
  

  useEffect(() => {
    if (!token) {
      navigate('/login');
    }
  }, [token, navigate]);

  if (!token) {
    return null;
  }



  if (isLoading) {
    return (
      <>
      <div className="flex justify-center items-center h-screen">
        <SyncLoader color="#3B82F6" size = "15" />
      </div>
      </>
    ) 
    
  }

  if (error) {
    return <div>An error occurred, please refresh the page</div>;
  }

  return (
    <>
  
    
   
    <DashboardContainer>
      {isSuccess && todos.myTodos && todos.myTodos.length > 0 ? (
        <TodoWrapper>
          {todos.myTodos.map((todo: Todo) => (
            <NewTodo key={todo._id} todo={todo} />
          ))}
        </TodoWrapper>
      ) : (
        <p>No todos found</p>
      )}
    </DashboardContainer>
    </>
  );
};

export default Dashboard;
